import React, { useState, useEffect } from 'react';

function Agents() {
  const [agents, setAgents] = useState([]);
  const [form, setForm] = useState({ nom: '', prenom: '' });

  useEffect(() => {
    fetch('http://localhost:5050/agents')
      .then(res => res.json())
      .then(data => setAgents(data));
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = e => {
    e.preventDefault();
    fetch('http://localhost:5050/agents', {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    })
      .then(res => res.json())
      .then(() => {
        setForm({ nom: '', prenom: '' });
        fetch('http://localhost:5050/agents')
          .then(res => res.json())
          .then(data => setAgents(data));
      });
  };

  return (
    <div>
      <h2>Gestion des agents</h2>
      <form onSubmit={handleSubmit}>
        <input name="nom" placeholder="Nom" value={form.nom} onChange={handleChange} required />
        <input name="prenom" placeholder="Prénom" value={form.prenom} onChange={handleChange} required />
        <button type="submit">Ajouter</button>
      </form>
      <ul>
        <table border="1" cellPadding={4} cellSpacing={0}>
  <thead>
    <tr>
      <th>Nom</th>
      <th>Prénom</th>
      <th>Poste</th>
      <th>Type de contrat</th>
      <th>Date d'entrée</th>
      <th>Date de sortie</th>
    </tr>
  </thead>
  <tbody>
    {agents.map(a => (
      <tr key={a.id}>
        <td>{a.nom}</td>
        <td>{a.prenom}</td>
        <td>{a.poste}</td>
        <td>{a.type_contrat}</td>
        <td>{a.date_entree ? a.date_entree : ''}</td>
        <td>{a.date_sortie ? a.date_sortie : ''}</td>
      </tr>
    ))}
  </tbody>
</table>
      </ul>
    </div>
  );
}
export default Agents;
